drop table av_order_invoice;CREATE TABLE IF NOT EXISTS `av_order_invoice` (  `id_order_invoice` int(11) NOT NULL AUTO_INCREMENT,  `id_order` int(11) NOT NULL,  `invoice_date` datetime NOT NULL,  PRIMARY KEY (`id_order_invoice`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;insert into av_order_invoice (id_order_invoice, id_order, invoice_date)SELECT id_order, id_order, invoice_date FROM `av_orders` where current_state between 2 and 7order by id_order;ALTER TABLE  `av_order_invoice` AUTO_INCREMENT =2760;ALTER TABLE  `av_order_invoice` ADD INDEX (  `id_order` );